import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom'



const UserDetail = () => {
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([{}])

    useEffect(() => {
        fetch('data/users.json').then((res) => res.json()).then((data) => {
            setUsers(data)
        })

    }, []);

    const getData = async () => {
        try {
            const response = await fetch('./data/subscriptions.json');
            const actualData = await response.json();
            console.log(actualData);
            setData(actualData)
        }
        catch (error) {
            console.log(error);
        }
    }


    useEffect(() => {
        getData()
    }, []);

    const [subsData, setSubsData] = useState([]);
    const { id } = useParams();
    // console.log(id);
    const getuserDetail = async () => {
        try {
            // const response = await fetch(`http://localhost:3000/users/${id}`);
            const response = await fetch('https://raw.githubusercontent.com/anshumallik/users/main/subscriptions.json');
            const data = await response.json();
            console.log(data);
            setSubsData(data)
        }
        catch (error) {
            console.log(error);
        }
    }

    useEffect(() => {
        getuserDetail();
    });
    return (
        <div className='content-wrapper'>
            <div className="container content user_detail">
                <div className='card col-md-6'>
                    <div className="card-header">
                        <div className="card-title mb-0">
                            <span className="content-header">User Name</span>
                        </div>
                    </div>
                    <div className='plan-detail pt-3'>
                        {subsData.map((currElem) => {
                            const user = subsData.find(uid => uid.id === Number(currElem.user_id)); 
                            return (
                                <div key={user.id}>
                                    <h6><strong>Package</strong> : {user.package}</h6>
                                    <h6><strong>Expiry Date</strong> : {user.expires_on}</h6>
                                </div>
                            )
                        })}


                    </div>
                </div>
            </div>
        </div>
    )
}

export default UserDetail;
