import React, { useEffect, useState } from "react";
import _ from 'lodash';

const pageSize = 10;
const Suscriber = () => {

    const [query, setQuery] = useState("");
    const [order, setorder] = useState("ASC");
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([{}])

    const sorting = (col) => {
        if (order === "ASC") {
            const sorted = [...data].sort((a, b) =>
                a[col].toString().toLocaleLowerCase() > b[col].toString().toLocaleLowerCase() ? 1 : -1

            );
            setData(sorted);
            setorder("DSC");
        }
        if (order === "DSC") {
            const sorted = [...data].sort((a, b) =>
                a[col].toString().toLocaleLowerCase() < b[col].toString().toLocaleLowerCase() ? 1 : -1

            );
            setData(sorted);
            setorder("ASC");
        }

    }
    useEffect(() => {
        fetch('data/users.json').then((res) => res.json()).then((data) => {
            setUsers(data)
        })

    }, []);

    const getData = async () => {
        try {
            const response = await fetch('./data/subscriptions.json');
            const actualData = await response.json();
            console.log(actualData);
            setData(actualData)
        }
        catch (error) {
            console.log(error);
        }
    }


    useEffect(() => {
        getData()
    }, []);

    const pageCount = data ? Math.ceil(data.length / pageSize) :0;
    if (pageCount === 1) return null;
    const pages =  _.range(1,pageCount+1)
    return (
        <div className="content-wrapper">

            <div className="container content">
                <div className='card rounded-0'>
                    <div className="card-header">
                        <div className="card-title mb-0">
                            <span className="content-header">Subscribed User Data List</span>
                        </div>
                    </div>
                    <div className='card-body'>
                        <div className="search_data">
                            <div className="float-end mb-2">
                                <label>
                                    Search:
                                </label>
                                <input type="search"
                                    className="input-field ms-2"
                                    placeholder="Search..."
                                    onChange={(e) => setQuery(e.target.value)}
                                />
                            </div>

                        </div>
                        <table className="table table-hover table-responsive-xl" id="subsTable">
                            <thead className="table-light">
                                <tr>
                                    <th scope="col" onClick={() => sorting("id")}>ID <i class="fas fa-sort sortings"></i></th>
                                    <th scope="col" onClick={() => sorting("user_id")}>User ID <i class="fas fa-sort sortings"></i></th>
                                    <th scope="col">Username <i class="fas fa-sort sortings"></i></th>
                                    <th scope="col" onClick={() => sorting("package")}>Package <i class="fas fa-sort sortings"></i></th>
                                    <th scope="col" onClick={() => sorting("expires_on")}>Expire on <i class="fas fa-sort sortings"></i></th>
                                </tr>
                            </thead>
                            <tbody>

                                {data.filter((val) => {
                                    if (query === "") {
                                        return val
                                    }
                                    else if (
                                        val.id.toString().toLocaleLowerCase().includes(query.toString().toLocaleLowerCase())
                                        || val.user_id.toString().toLocaleLowerCase().includes(query.toLocaleLowerCase())
                                        || val.package.toLocaleLowerCase().includes(query.toString().toLocaleLowerCase())
                                        || val.expires_on.toString().toLocaleLowerCase().includes(query.toLocaleLowerCase())
                                    ) {
                                        return val
                                    }
                                    return false;
                                }).map((currElem, index) => {

                                    const user = users.find(uid => uid.id === Number(currElem.user_id));

                                    return <tr key={index}>
                                        <td>{currElem.id}</td>
                                        <td>{currElem.user_id}</td>
                                        <td>{user?.username || '-'} </td>
                                        <td>{currElem.package}</td>
                                        <td>{currElem.expires_on}</td>

                                    </tr>
                                })}
                            </tbody>
                        </table>
                        <nav className="d-flex float-end">
                            <ul className="pagination">
                            {
                                pages.map()
                            }
                                <li className="page-link">1</li>
                                
                            </ul>
                        </nav>
                    </div>
                </div>
            </div>
        </div>

    )
}


export default Suscriber;
