import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom'



const UserDetail = () => {
    const [subsData, setSubsData] = useState([]);
    const [data, setData] = useState([]);
    const { id } = useParams();
    // console.log(id);
    const getuserDetail = async () => {
        try {
            const response = await fetch(`${id}`, {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            });
            var stringified = JSON.stringify(response);
            var parsedObj = JSON.parse(stringified);
            console.log(parsedObj);
            // const data = await response.json();
            setSubsData(response)
        }
        catch (error) {
            console.log(error);
        }
    }
    const getData = async () => {
        try {
            const response = await fetch('./data/subscriptions.json');
            const actualData = await response.json();
            console.log(actualData);
            setData(actualData)
        }
        catch (error) {
            console.log(error);
        }
    }
    useEffect(() => {
        getuserDetail();
    });
    useEffect(() => {
        getData()
    });

    return (
        <div className='content-wrapper'>
            <div className="container content user_detail">
                <div className='card col-md-6'>
                    <div className="card-header">
                        <div className="card-title mb-0">
                            <span className="content-header"><strong>User Detail</strong></span>
                        </div>
                    </div>
                    <div className='plan-detail pt-3'>
                        {data.map((currElem) => {
                            const user = subsData.filter(id === Number(currElem.user_id));
                            console.log(user);
                            return (
                                <div key={currElem.id}>
                                    <div className="row">
                                        <div className="col-md-3">
                                            <h6><strong>Name</strong> </h6>
                                            <h6><strong>User Name</strong></h6>
                                            <h6><strong>Email</strong> </h6>
                                            <h6><strong>Password</strong> </h6>
                                            <h6><strong>Address</strong> </h6>
                                            <h6><strong>Country</strong>  </h6>
                                            <h6><strong>Join Date</strong> </h6>
                                        </div>
                                        <div className="col-md-1">
                                            <h6>: </h6>
                                            <h6>:</h6>
                                            <h6>:</h6>
                                            <h6>:</h6>
                                            <h6>: </h6>
                                            <h6>: </h6>
                                            <h6>:</h6>
                                        </div>
                                        <div className="col-md-8">

                                            <h6> {user?.package || '-'}</h6>


                                        </div>

                                    </div>
                                </div>
                            )
                        })

                        }



                    </div>
                </div>
            </div>
        </div>
    )
}

export default UserDetail;
