import React, { useEffect, useState } from "react";



const Suscriber = () => {

    const [data, setData] = useState([]);
    const [users, setUsers] = useState([{}])

    useEffect(() => {
        fetch('data/users.json').then((res) => res.json()).then((data) => {
            setUsers(data)
        })

    }, [])

    const getData = () => {
        fetch('./data/subscriptions.json'
            , {
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                }
            }
        )
            .then(function (response) {
                console.log(response)
                return response.json();
            })
            .then(function (myJson) {
                console.log(myJson);
                setData(myJson)
            });
    }

    useEffect(() => {
        getData()
    }, [])



    return (
        <div className="content-wrapper">

      <div className="container content">
        <div className='card rounded-0'>
          <div className="card-header">
            <div className="card-title mb-0">
              <span className="content-header">All Users</span>
            </div>
          </div>
          <div className='card-body'>
            <div className="table-responsive">
              <table id="UserTable" className="table table-hover table-responsive-xl">
                <thead className="table-light">
                  <tr>
                    <th>S.N</th>
                    <th>Name</th>
                    <th>User Name</th>
                    <th>Email</th>
                    <th>Password</th>
                    <th>Address</th>
                    <th>Country</th>
                    <th>Join Date</th>
                    <th>Status</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {
                    user.map((currElem) => {
                      return (
                        <tr key={currElem.id}>
                          <td>{currElem.id}</td>
                          <td>{currElem.first_name + " " + currElem.middle_name + " " + currElem.last_name}</td>
                          <td>{currElem.username}</td>
                          <td>{currElem.email}</td>
                          <td>{currElem.password}</td>
                          <td>{currElem.address}</td>
                          <td>{currElem.country}</td>
                          <td>{currElem.join_date}</td>
                          <td>
                            
                            {(currElem.active === '1') ? (
                              <span className="badge bg-success">Active</span>) : (
                                <span className="badge bg-warning">Inactive</span>
                            )}
                            
                            
                          </td>
                          <td>
                            <button className="btn view-btn">
                              <Link to={`/user-detail/${currElem.id}`}>View</Link>
                            </button>
                          </td>
                        </tr>
                      );
                    })
                  }

                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>
    </div>
       
    )
}


export default Suscriber;
