import React, { useEffect, useState } from "react";
import 'jquery/dist/jquery.min.js';
//Datatable Modules
import "datatables.net-dt/js/dataTables.dataTables"
import "datatables.net-dt/css/jquery.dataTables.min.css"
import $ from 'jquery';


const Suscriber = () => {

    const [data, setData] = useState([]);
    const [users, setUsers] = useState([{}])

    useEffect(() => {
        fetch('data/users.json').then((res) => res.json()).then((data) => {
            setUsers(data)
        })

    }, []);

    const getData = async () => {
        try {
            const response = await fetch('./data/subscriptions.json');
            const actualData = await response.json();
            console.log(actualData);
            setData(actualData)
        }
        catch (error) {
            console.log(error);
        }
    }


    useEffect(() => {
        getData()
    }, []);

    useEffect(() => {
        setTimeout(() => {
            $("#subsTable").DataTable();
        }, 1000)
    }, []);

    return (
        <div className="content-wrapper">

            <div className="container content">
                <div className='card rounded-0'>
                    <div className="card-header">
                        <div className="card-title mb-0">
                            <span className="content-header">All Users</span>
                        </div>
                    </div>
                    <div className='card-body'>
                        <div className="table-responsive">
                            <table className="table table-hover table-responsive-xl" id="subsTable">
                                <thead>
                                    <tr>
                                        <th scope="col">ID</th>
                                        <th scope="col">User ID</th>
                                        <th scope="col">Username</th>
                                        <th scope="col">Package</th>
                                        <th scope="col">Expire on</th>
                                    </tr>
                                </thead>
                                <tbody>

                                    {data.map((currElem, index) => {

                                        const user = users.find(uid => uid.id === Number(currElem.user_id));

                                        return <tr key={index}>
                                            <td>{currElem.id}</td>
                                            <td>{currElem.user_id}</td>
                                            <td data-label="Username">{user?.username || '-'} </td>
                                            <td data-label="Package">{currElem.package}</td>
                                            <td data-label="Expire on">{currElem.expires_on}</td>

                                        </tr>
                                    })}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
            </div>
        </div>

    )
}


export default Suscriber;
